(function() {
  var csv, fs, should;
  fs = require('fs');
  should = require('should');
  csv = require('..');
  describe('write', function() {
    it('Test write array', function(next) {
      var count, i, test;
      count = 0;
      test = csv().toPath("" + __dirname + "/write/write_array.tmp").on('data', function(data, index) {
        data.should.be.an["instanceof"](Array);
        count.should.eql(index);
        return count++;
      }).on('end', function() {
        var expect, result;
        count.should.eql(1000);
        expect = fs.readFileSync("" + __dirname + "/write/write.out").toString();
        result = fs.readFileSync("" + __dirname + "/write/write_array.tmp").toString();
        result.should.eql(expect);
        return fs.unlink("" + __dirname + "/write/write_array.tmp", next);
      });
      for (i = 0; i < 1000; i++) {
        test.write(["Test " + i, i, '"']);
      }
      return test.end();
    });
    it('Test write object with column options', function(next) {
      var count, i, test;
      count = 0;
      test = csv().toPath("" + __dirname + "/write/write_object.tmp", {
        columns: ['name', 'value', 'escape']
      }).on('data', function(data, index) {
        data.should.be.a('object');
        data.should.not.be.an["instanceof"](Array);
        count.should.eql(index);
        return count++;
      }).on('end', function() {
        var expect, result;
        count.should.eql(1000);
        expect = fs.readFileSync("" + __dirname + "/write/write.out").toString();
        result = fs.readFileSync("" + __dirname + "/write/write_object.tmp").toString();
        result.should.eql(expect);
        return fs.unlink("" + __dirname + "/write/write_object.tmp", next);
      });
      for (i = 0; i < 1000; i++) {
        test.write({
          name: "Test " + i,
          value: i,
          escape: '"',
          ovni: "ET " + i
        });
      }
      return test.end();
    });
    it('Test write string', function(next) {
      var buffer, count, i, test;
      count = 0;
      test = csv().toPath("" + __dirname + "/write/write_string.tmp").on('data', function(data, index) {
        data.should.be.an["instanceof"](Array);
        count.should.eql(index);
        return count++;
      }).on('end', function() {
        var expect, result;
        count.should.eql(1000);
        expect = fs.readFileSync("" + __dirname + "/write/write.out").toString();
        result = fs.readFileSync("" + __dirname + "/write/write_string.tmp").toString();
        result.should.eql(expect);
        return fs.unlink("" + __dirname + "/write/write_string.tmp", next);
      });
      buffer = '';
      for (i = 0; i < 1000; i++) {
        buffer += ''.concat("Test " + i, ',', i, ',', '""""', "\n");
        if (buffer.length > 250) {
          test.write(buffer.substr(0, 250));
          buffer = buffer.substr(250);
        }
      }
      test.write(buffer);
      return test.end();
    });
    it('Test write string with preserve', function(next) {
      var buffer, count, i, test;
      count = 0;
      test = csv().toPath("" + __dirname + "/write/string_preserve.tmp").transform(function(data, index) {
        if (index === 0) {
          test.write('--------------------\n', true);
        }
        test.write(data);
        test.write('\n--------------------', true);
        data.should.be.an["instanceof"](Array);
        count.should.eql(index);
        count++;
        return null;
      }).on('end', function() {
        var expect, result;
        expect = fs.readFileSync("" + __dirname + "/write/string_preserve.out").toString();
        result = fs.readFileSync("" + __dirname + "/write/string_preserve.tmp").toString();
        result.should.eql(expect);
        return fs.unlink("" + __dirname + "/write/string_preserve.tmp", next);
      });
      test.write('# This line should not be parsed', true);
      test.write('\n', true);
      buffer = '';
      for (i = 0; i < 2; i++) {
        buffer += ''.concat("Test " + i, ',', i, ',', '""""', "\n");
        if (buffer.length > 250) {
          test.write(buffer.substr(0, 250));
          buffer = buffer.substr(250);
        }
      }
      test.write(buffer);
      test.write('\n', true);
      test.write('# This one as well', true);
      return test.end();
    });
    it('should transform data provided by write as an array', function(next) {
      var count, i, test;
      count = 0;
      test = csv().toPath("" + __dirname + "/write/write_array.tmp").transform(function(data, index) {
        return count++;
      }).on('end', function() {
        count.should.eql(1000);
        return fs.unlink("" + __dirname + "/write/write_array.tmp", next);
      });
      for (i = 0; i < 1000; i++) {
        test.write(['Test ' + i, i, '"']);
      }
      return test.end();
    });
    return it('should emit header even without a source', function(next) {
      var test;
      test = csv().toPath("" + __dirname + "/write/write_sourceless.tmp", {
        columns: ['col1', 'col2'],
        header: true,
        lineBreaks: 'unix'
      }).on('end', function() {
        var expect, result;
        expect = fs.readFileSync("" + __dirname + "/write/write_sourceless.out").toString();
        result = fs.readFileSync("" + __dirname + "/write/write_sourceless.tmp").toString();
        result.should.eql(expect);
        return fs.unlink("" + __dirname + "/write/write_sourceless.tmp", next);
      });
      test.write({
        col1: 'foo1',
        col2: 'goo1'
      });
      test.write({
        col1: 'foo2',
        col2: 'goo2'
      });
      return test.end();
    });
  });
}).call(this);
